import React, { Component } from "react";
import axios from "axios";
import Avatar from "./Avatar";

class ImgUpload extends Component {
  //to store the picture
  state = { selectedFile: null };

  fileSelectedHandler = (event) => {
    this.setState({ selectedFile: event.target.files[0] });
    console.log(event.target.files[0]);
  };

  fileUploadHandler = () => {
    axios.get('http://localhost:5000/cache')
    .then(res => {
      data = res["data"];
    })
    .catch(function (e) {
    //Not handling the error. Just logging into the console.
      console.log(e);
    });
    const fd = new FormData();
    fd.append("image", this.state.selectedFile, this.state.selectedFile.name);
    //needs to send an http request
    axios
      .put("http://localhost:5000/upload" + data["_id"], fd, {
        onUploadProgress: (progressEvent) => {
          console.log(
            "Upload Progress:" +
              Math.round((progressEvent.loaded / progressEvent.total) * 100) +
              "%"
          );
        }
      })
      .then((res) => {
        console.log(res);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  render() {
    return (
      <div className="ImgUpload">
        <input
          style={{ display: "none" }}
          type="file"
          onChange={this.fileSelectedHandler}
          ref={(fileInput) => (this.fileInput = fileInput)}
        />
        <button onClick={() => this.fileInput.click()}>Pick File</button>
        <button onClick={this.fileUploadHandler}>Upload Your Picture</button>
        <div>
        <Avatar img="https://picsum.photos/200" />
        </div>
      </div>
    );
  }
}

export default ImgUpload;
